mapper.select:
SELECT a.*,b.user_name,d.username create_user_name ,c.account_manager_no ,c.account_manager_name, b.amount,b.account_month,b.address,b.user_code from tb_task a LEFT JOIN tb_charge_overdue b ON a.charge_id=b.ID LEFT JOIN sys_user c ON a.account_manager_no = c.account_manager_no left join sys_user d on a.create_user_id = d.user_id
(b.user_name like concat('%',#{userName},'%') OR c.account_manager_no like concat('%',#{userName},'%') OR c.account_manager_name like concat('%',#{userName},'%'))
c.user_id = #{userId}
AND a.result = #{status}
order by a.ID desc
limit #{offset}, #{limit}

SELECT count(1) from tb_task a LEFT JOIN tb_charge_overdue b ON a.charge_id=b.ID LEFT JOIN sys_user c ON a.account_manager_no = c.account_manager_no
(b.user_name like concat('%',#{userName},'%') OR c.account_manager_no like concat('%',#{userName},'%') OR c.account_manager_name like concat('%',#{userName},'%'))
c.user_id = #{userId}
AND a.result = #{status}
